import React, { useEffect, useState } from "react";

const CapturingFlagComponent = () => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [visibleChars, setVisibleChars] = useState([]);
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          "https://wgg522pwivhvi5gqsn675gth3q0otdja.lambda-url.us-east-1.on.aws/616e79"
        );

        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const result = await response.text();
        console.log(result);
        setData(result);
      } catch (error) {
        console.log(error);
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      if (currentIndex < data.length) {
        setCurrentIndex(currentIndex + 1);
      } else {
        clearInterval(interval);
      }
    }, 500);

    return () => clearInterval(interval);
  }, [currentIndex, data]);

  useEffect(() => {
    setChars(data.split(""));

    const intervalId = setInterval(() => {
      if (visibleChars.length < chars.length) {
        setVisibleChars([...visibleChars, chars[visibleChars.length]]);
      } else {
        clearInterval(intervalId);
      }
    }, 100); // Adjust the delay as needed

    return () => clearInterval(intervalId);
  }, [data]);
  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;
  return (
    <div>
      <ul>
        {visibleChars.map((char, index) => (
          <li key={index}>
            <span>{index === 0 ? "_" : char}</span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CapturingFlagComponent;
