import React, { useEffect, useState } from "react";

const CapturingFlagComponent = () => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [newText, setText] = useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          "https://wgg522pwivhvi5gqsn675gth3q0otdja.lambda-url.us-east-1.on.aws/616e79"
        );

        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const result = await response.text();
        console.log(result);
        setData(result);
      } catch (error) {
        console.log(error);
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []); // Empty dependency array means this runs once on mount

  useEffect(() => {
    const interval = setInterval(() => {
      if (currentIndex < data.length) {
        setCurrentIndex(currentIndex + 1);
      } else {
        clearInterval(interval);
      }
    }, 500); // Adjust the delay as needed

    return () => clearInterval(interval);
  }, [currentIndex, data]);
  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;
  return (
    <div>
      <ul style={{ "list-style-type": "none" }}>
        {data
          .slice(0, currentIndex)
          .split("")
          .map((char, index) => (
            <li style={{ display: "inline" }} key={index}>
              {index < currentIndex ? "x" : "_"}
            </li>
          ))}
      </ul>
    </div>
  );
};

export default CapturingFlagComponent;
